definition.script:
pipeline {
    agent{
        label 'master'
        }
    options {
        timestamps()
        ansiColor('xterm')
    }
    stages {
        stage('download files from git') {
            steps{
                 sh "git clone  https://github.com/karpovichart/devops.git"
                 sh cd home/jenkins/workspace/test/devops
                 sh "git checkout dev"
                }
        }
        stage('copy folder to dest') {
            steps{
                 sh cp /home/jenkins/workspace/test/devops/ansible/pb_conf_wp.yml  /home/ubuntu/devops/ansible
                 sh cp /home/jenkins/workspace/test/devops/ansible/ansible.cfg  /home/ubuntu/devops/ansible
                 sh cp -r /home/jenkins/workspace/test/devops/ansible/roles  /home/ubuntu/devops/ansible
             }
        }
        stage('running ansible playbook') {
            steps {
                ansiblePlaybook(
                becomeUser: 'tomcat',
                colorized: true,
                inventory: '/home/jenkins/workspace/ansible/devops/ansible/hosts.ini',
                playbook: '/home/ubuntu/devops/ansible/pb_conf_wp.yml')
            }
        }
    }
}
